WITH const AS 
	  (SELECT   1 AS day_id, STR_TO_DATE("07 11 2020","%d %m %Y") AS day_n
	   UNION
		SELECT   2 AS day_id, STR_TO_DATE("08 11 2020","%d %m %Y") AS day_n
	   UNION
		SELECT   3 AS day_id, STR_TO_DATE("09 11 2020","%d %m %Y") AS day_n
	   UNION
		SELECT   4 AS day_id, STR_TO_DATE("10 11 2020","%d %m %Y") AS day_n
	   UNION
		SELECT   5 AS day_id, STR_TO_DATE("11 11 2020","%d %m %Y") AS day_n
	   UNION
		SELECT   6 AS day_id, STR_TO_DATE("12 11 2020","%d %m %Y") AS day_n
	  )
SELECT 	ui.leaderID, const.day_n,
			project_presentation_user.projects, project_presentation_user.presentations,
			project_tags_user.market, project_tags_user.method, project_tags_user.tech, project_tags_user.task,
			task_edu_user.Tasks_Created, task_edu_user.Tasks_Done, task_edu_user.Tasks_Validated, task_edu_user.EDUreq_Created, task_edu_user.EDUreq_Done
FROM  labs.user_info AS ui 
JOIN const
LEFT JOIN ( 
			SELECT leaderID, COUNT(p_id) AS projects, SUM(presentations) AS presentations, const.day_n
			FROM const
				,(
					SELECT ui.leaderID, p.id AS p_id, p.title, count(distinct pp.id) AS presentations, pp.createDT
					FROM people.project AS p 
					LEFT JOIN people.project_presentation AS pp ON pp.projectID=p.id
					LEFT JOIN people.project_team AS prt ON prt.projectID=p.id
					LEFT JOIN people.team AS t ON prt.teamID=t.id
					LEFT JOIN people.team_user AS tu ON tu.teamID=prt.teamID
					LEFT JOIN people.user_info AS ui ON ui.userID=tu.userID
					WHERE ui.leaderID IS NOT NULL
					GROUP BY ui.leaderID, p.id, pp.createDT
					) AS project_presentation_by_project_user
			WHERE project_presentation_by_project_user.createDT>=const.day_n AND  project_presentation_by_project_user.createDT<const.day_n+1
			GROUP BY leaderID, const.day_n
			) as project_presentation_user
		ON project_presentation_user.leaderID=ui.leaderID AND project_presentation_user.day_n=const.day_n
LEFT JOIN (
					SELECT  ui.leaderID, const.day_n, 
								SUM(pt.type='market') AS market,	SUM(pt.type='method') AS method,	SUM(pt.type='tech') AS tech, SUM(pt.type='task') AS task
								FROM const, people.project AS p
								LEFT JOIN people.project_tag AS pt ON pt.projectID=p.id
								LEFT JOIN people.project_team AS prt ON prt.projectID=p.id
								LEFT JOIN people.team AS t ON prt.teamID=t.id
								LEFT JOIN people.team_user AS tu ON tu.teamID=prt.teamID
								LEFT JOIN people.user_info AS ui ON ui.userID=tu.userID
								WHERE ui.leaderID IS NOT NULL AND pt.createDT>=const.day_n AND  pt.createDT<const.day_n+1
								GROUP BY ui.leaderID,  pt.createDT
			) AS  project_tags_user
		ON project_tags_user.leaderID=ui.leaderID	 AND project_tags_user.day_n=const.day_n
LEFT JOIN (	
				SELECT ui.leaderID , const.day_n, sc.createDT,sc.dt,
						SUM(sc.status='new' AND sc.type ='task_team') AS Tasks_Created,  SUM(sc.status='done' AND sc.type ='task_team') AS Tasks_Done, 
						SUM(sc.status='validated' AND sc.type ='task_team') AS Tasks_Validated, SUM(sc.status='validated_error' AND sc.type ='task_team') AS Tasks_Validated_error,
						SUM(sc.status='new' AND sc.type ='focus_team') AS EDUreq_Created,  SUM(sc.status='done' AND sc.type ='focus_team') AS EDUreq_Done
				FROM const, ple.step_collection sc
				LEFT JOIN ple.user_info ui ON sc.untiID=ui.untiID
				WHERE sc.type IN ('task_team','focus_team') AND 
						IF(sc.dt IS NOT NULL, sc.dt, sc.createDT)>=const.day_n AND IF(sc.dt IS NOT NULL, sc.dt, sc.createDT)<const.day_n+1 
				GROUP BY ui.leaderID, const.day_n
				) AS  task_edu_user
		ON task_edu_user.leaderID=ui.leaderID	AND task_edu_user.day_n=const.day_n
					
